import logo from './logo.svg';
import React, { useState } from 'react';
import './App.css';

import ContributionInput from './ContributionInput';  // Import the new component



function App() {

  const [type, setType] = useState('dollar');

  const [dollarAmount, setDollarAmount] = useState(200);

  const [percentage, setPercentage] = useState(5);



  return (
    <div className="App">
      <title> My 401(k) Contributions </title>
      <h1> My 401(k) Contributions </h1>


      <h2 class="section-title">Select how you would like to contribute to your 401(k)</h2>
      <div class="button-container">
        <button class="option-btn" onClick={() => setType('dollar')}>Fixed Dollar Amount</button>
        <button class="option-btn" onClick={() => setType('percentage')}>Percentage of Paycheck</button>
      </div>






    </div>

    );




}

export default App;
