import logo from './logo.svg';
import React, { useState } from 'react';
import './App.css';
import Slider from './Slider';



function App() {

  const [type, setType] = useState('dollar');

  const [dollarAmount, setDollarAmount] = useState(200);

  const [percentage, setPercentage] = useState(5);

  const mockData = {
    salary: 75000,        
    ytdContributions: 4500,     
    payPeriods: 24,               
    employerMatch: 1200,          
  };
  const pay = mockData.annualSalary / mockData.payPeriods; 

  return (
    <div className="App">
      <title> My 401(k) Contributions </title>
      <h1> My 401(k) Contributions </h1>


      <h2 class="section-title">Select how you would like to contribute to your 401(k)</h2>
      <div class="button-container">
        <button class="option-btn" onClick={() => setType('dollar')}>Fixed Dollar Amount</button>
        <button class="option-btn" onClick={() => setType('percentage')}>Percentage of Paycheck</button>
      </div>

      {type === 'dollar' && (
        <div className="slider-container">
          <p>Amount per paycheck: ${dollarAmount}</p>
          <Slider
            value={dollarAmount}
            onChange={setDollarAmount}
            min={0}
            max={3125}
            step={10}
          />
        </div>
      )}

      {type === 'percentage' && (
        <div className="slider-container">
          <p>Percentage of paycheck: {percentage}%</p>
          <Slider
            value={percentage}
            onChange={setPercentage}
            min={0}
            max={20}
            step={0.5}
          />
        </div>
      )}

      <div className = "summary">
        <h2>Your Account Summary</h2>
        <p>Your Salary: ${mockData.salary}</p>
        <p>Saved This Year: ${mockData.ytdContributions}</p>
      </div>

      </div>

    );




}

export default App;
