import logo from './logo.svg';
import React, { useState } from 'react';
import './App.css';
import Slider from './Slider';
import Summary from './Summary';  // Import the Summary component


function App() {
  const [type, setType] = useState('dollar');
  const [dollarAmount, setDollarAmount] = useState(200);
  const [percentage, setPercentage] = useState(5);

  // Mock data
  const salary = 75000;
  const payPeriods = 24;
  const payPerPeriod = salary / payPeriods; // $3,125 per paycheck

  // Calculate contribution per paycheck based on slider
  let contributionPerPaycheck = 0;
  if (type === 'dollar') {
    contributionPerPaycheck = dollarAmount;
  } else {
    contributionPerPaycheck = (percentage / 100) * payPerPeriod;
  }

  // Calculate total saved this year (10 paychecks so far)
  const totalSavedThisYear = contributionPerPaycheck * 10;

  return (
    <div className="App">
      <h1>My 401(k) Contributions</h1>

      {/* Summary Card */}
      <Summary
        salary={salary}
        totalSavedThisYear={totalSavedThisYear}
        contributionPerPaycheck={contributionPerPaycheck}
      />


      {type === 'dollar' && (
        <div className="slider-container">
          <p>Amount per paycheck: ${dollarAmount}</p>
          <Slider
            value={dollarAmount}
            onChange={setDollarAmount}
            min={0}
            max={1000}
            step={10}
          />
        </div>
      )}

      {type === 'percentage' && (
        <div className="slider-container">
          <p>Percentage of paycheck: {percentage}%</p>
          <Slider
            value={percentage}
            onChange={setPercentage}
            min={0}
            max={20}
            step={0.5}
          />
        </div>
      )}
    </div>
  );
}

export default App;
