import Slider from './Slide';
import React, { useState } from 'react';

function Contribution({ type, dollarAmount, setDollarAmount, percentage, setPercentage }) {
  return (
    <div>
      {type === 'dollar' && (
        <div className="slider-container">
          <p>Amount per paycheck: ${dollarAmount}</p>
          <Slider
            value={dollarAmount}
            onChange={setDollarAmount}
            min={0}
            max={1000}
            step={10}
          />
        </div>
      )}

      {type === 'percentage' && (
        <div className="slider-container">
          <p>Percentage of paycheck: {percentage}%</p>
          <Slider
            value={percentage}
            onChange={setPercentage}
            min={0}
            max={20}
            step={0.5}
          />
        </div>
      )}
    </div>
  );
}

export default Contribution;
